const figures=document.querySelectorAll(".story-fig"),totalPages=figures.length;let current=0;function showSpread(e){figures.forEach(e=>e.style.display="none");for(let t=e;t<e+2&&t<totalPages;t++)figures[t].style.display="inline-block"}document.getElementById("prev").addEventListener("click",()=>{current=Math.max(0,current-2),showSpread(current)}),document.getElementById("next").addEventListener("click",()=>{current=current+2,current>=totalPages&&(current=totalPages-(totalPages%2===0?2:1)),showSpread(current)}),showSpread(current)